sys_script_include.script:
function englishTimeConverter(date){
	//import date as a GlideDateTime obj
	var gdt=new GlideDateTime(date);

	//getting time and date from date GDT obj, then converting to the chosen format
	var time=gdt.getLocalTime();
	var timeStr=time.getByFormat('hh:mm a');
	var day=gdt.getLocalDate();
	var dayStr=day.getByFormat('dd/MM/yyyy');

	//returning the string with the chosen date/time format
	var dateTime=dayStr + ' ' + timeStr;
	//gs.addInfoMessage(dateTime);
	return dateTime;

}
